import React, { useEffect, useState } from 'react';
import { useForm, Controller, SubmitHandler } from "react-hook-form";
import { FormattedMessage } from 'react-intl';
import toast, { Toaster } from 'react-hot-toast';
import { ILoginParams, ILoginValidation, ISingupParams } from '../../../models/auth';
import logo from '../../../logo-420-x-108.png';

interface Props {
    // onLogin(values: ILoginParams): void;
    loading: boolean;
    errorMessage: string;
}


const SingupForm = (props: Props) => {
    const { loading, errorMessage } = props;
    const [city, setCity] = useState([])
    console.log(city)
    const [formValues, setFormValues] = React.useState<ISingupParams>({
        email: '',
        password: '',
        register: "",
        fullName: "",
        nation: "",
        city: "",
    });
    const { control, handleSubmit } = useForm({
        defaultValues: {
            email: '',
            password: '',
            register: "",
            fullName: "",
            nation: "",
            city: "",

        }
    });

    useEffect((
        () => {
            fetch('api.training.div3.pgtest.co/api/v1/location',

            )
                .then(response => response.json())
                .then(data =>
                    console.log(data))
            // setCity(data)
        }
    ), [])


    function toastMessage() {
        if (errorMessage.trim() === "Login succes") {
            toast.success("Login sucess!", {
                duration: 2000,
            })

        } else {
            toast.error("Invalid username / password", {
                duration: 1000,
            })
        }

    }


    const onSubmit = React.useCallback(async (data: ILoginParams) => {
        // await onLogin(formValues)

        toastMessage()

    }, [formValues]);

    return (
        <div
            className="container"
            style={{
                height: '100vh',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flexDirection: 'column',
            }}
        >
            <Toaster
                position="top-center"
                reverseOrder={false}
            />
            <img src={logo} alt="" style={{ maxWidth: '250px', margin: '32px' }} />


            <form
                // onSubmit={handleSubmit(onSubmit)}
                style={{ maxWidth: '560px', width: '100%' }}
                className="row g-3 needs-validation"
            >

                <Controller
                    name="email"
                    control={control}
                    defaultValue=""
                    rules={{
                        pattern: {
                            value: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                            message: "Value is not email"
                        },
                        required: "This email is required.",
                    }}
                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputEmail" className="form-label">
                                <FormattedMessage id="email" />
                            </label>
                            <input
                                {...field}
                                type="text"
                                name="email"
                                className="form-control"
                                id="inputEmail"
                                onChange={(e) => {
                                    field.onChange(e);
                                    setFormValues({ ...formValues, email: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error.message}
                            </small>}
                        </div>
                    )}
                />

                <Controller
                    name="password"
                    control={control}
                    defaultValue=""
                    rules={{
                        minLength: {
                            value: 6,
                            message: "This input must exceed 6 characters"
                        },
                        required: "This password is required.",

                    }}
                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="password" />
                            </label>
                            <input {...field}
                                type="password"
                                className="form-control"
                                id="inputPassword"
                                onChange={(e) => {

                                    field.onChange(e);
                                    setFormValues({ ...formValues, password: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />

                <Controller
                    name="register"
                    control={control}
                    defaultValue=""
                    rules={{
                        minLength: {
                            value: 6,
                            message: "This input must exceed 6 characters"
                        },
                        required: "This password is required.",

                    }}
                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="Xác nhập lại mật khẩu" />
                            </label>
                            <input {...field}
                                type="password"
                                className="form-control"
                                id="inputPassword"
                                onChange={(e) => {

                                    field.onChange(e);
                                    setFormValues({ ...formValues, password: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />
                <Controller
                    name="fullName"
                    control={control}

                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="Họ và Tên" />
                            </label>
                            <input {...field}
                                type="password"
                                className="form-control"
                                id="inputPassword"
                                onChange={(e) => {

                                    field.onChange(e);
                                    setFormValues({ ...formValues, password: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />


                <Controller
                    name="nation"
                    control={control}

                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="Giới tính" />
                            </label>
                            <select className="form-select" aria-label="Default select example">
                                <option selected>Open this select menu</option>
                                {city.map((ci) => {
                                    return <option >{ci}</option>
                                })}
                            </select>
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />

                <Controller
                    name="city"
                    control={control}

                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="Quốc Gia" />
                            </label>
                            <input {...field}
                                type="password"
                                className="form-control"
                                id="inputPassword"
                                onChange={(e) => {

                                    field.onChange(e);
                                    setFormValues({ ...formValues, password: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />

                <Controller
                    name="city"
                    control={control}

                    render={({ field, fieldState: { error } }) => (
                        <div className="col-md-12">
                            <label htmlFor="inputPassword" className="form-label">
                                <FormattedMessage id="Thành Phố" />
                            </label>
                            <input {...field}
                                type="password"
                                className="form-control"
                                id="inputPassword"
                                onChange={(e) => {

                                    field.onChange(e);
                                    setFormValues({ ...formValues, password: e.target.value })
                                }}
                            />
                            {error && <small className="text-danger">
                                {error?.message}</small>}
                        </div>
                    )}
                />

                <div className="row justify-content-md-center" style={{ margin: '16px 0' }}>
                    <div className="col-md-auto" style={{ position: "relative" }}>
                        <button
                            className="btn btn-primary"
                            type="submit"
                            style={{ minWidth: '160px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
                            disabled={loading}
                            value="Đăng Nhập"
                        >
                            {loading && <div className="spinner-border spinner-border-sm text-light mr-2" role="status" />}
                            <FormattedMessage id="register" />


                        </button>
                    </div>
                </div>
            </form >

        </div>

    );
};

export default SingupForm;
